const storedCount = localStorage.getItem("previousNotificationCount");
let initialCount = null;
let initialData = false;

try {
  initialCount = JSON.parse(storedCount);
  initialData = !!storedCount;
} catch (error) {
  console.error("Error parsing JSON:", error);
}

const initialState = {
  NotiCount: initialCount,
  data: initialData,
};

const authReducer = (state = initialState, action) => {
  switch (action.type) {
    case "Send_CountSUCCESS":
      localStorage.setItem("previousNotificationCount", action.payload);

      return {
        ...state,
        NotiCount: action.payload,
        data: true,
      };

    default:
      return state;
  }
};

export default authReducer;
