import React from "react";
import { useQuery } from "react-query";
import axios from "axios";
async function UpdateNoti(id) {
  try {
    await axios.put(`http://127.0.0.1:8000/api/notifications/${id}`, {
      open: "true",
    });
  } catch (error) {
    // Handle error, e.g., show an error message
    console.error("Error updating product:", error.message);
  }
}

const fetchNotifications = async () => {
  try {
    const response = await axios.get(
      "http://127.0.0.1:8000/api/notReednotifications"
    );
    console.log(response.data.notifications);
    return response.data.notifications;
  } catch (error) {
    throw new Error("Network response was not ok");
  }
};

const MakeNotiOpen = ({ isOpen }) => {
  const {
    data: notifications,
    isLoading,
    isError,
  } = useQuery("notifications", fetchNotifications);

  notifications.forEach((noti) => {
    try {
      UpdateNoti(noti.id);
    } catch {}
  });

  return isOpen ? <div>{/* Your notification UI here */}</div> : null;
};

export default MakeNotiOpen;
