import React, { useState } from "react";
import axios from "axios";

export default function ({ formData }) {
  console.log("ff");
  console.log("formDataformDataformData", formData);
  const [formDatanotification, setFormDatanotification] = useState({
    name: "ahmad",
    quantity: "",
    price: "",
    description: "",
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        "http://127.0.0.1:8000/api/notifications",
        formDatanotification
      );
      // Handle success, e.g., show a success message or redirect the user
      console.log("notifications added successfully");

      setFormDatanotification({
        name: "",
        quantity: "",
        price: "",
        description: "",
      });
    } catch (error) {
      // Handle error, e.g., show an error message
      console.error("Error adding notifications:", error.message);
    }
  };
}
